import React, { Component } from 'react';
import "./styles.css";

export default class binToDec extends Component {

    state = {
        binaryValue: null,
        decimalValue: null
    }

    convertValue = () => {
        const newDecimalValue = 10;
        this.setState({
            newDecimalValue
        });
    }

    render() {
        return (
            <div className="binToDec-form">
                <h2>Binary to Decimal Converter</h2>
                <form>
                    <label>Binary Input</label>
                    <br />
                    <input
                        type="text"
                        placeholder="0 ou 1"
                        name="binary"
                        value={this.state.binaryValue}
                    />
                    <br />
                    <label>Decimal Output</label>
                    <br />
                    <input
                        type="text"
                        name="decimal"
                        value={this.state.decimalValue}
                        onChange={this.convertValue}
                    />
                    <br />
                    <button onClick={this.bin2Dec}>Covert</button>
                </form>
                <p>If you click the "Submit" button, the form-data will be sent to a page called "/action_page.php".</p>
            </div>
        );
    }
}
