import React, { Component } from 'react';
import diceAPI from '../../services/diceAPI.js';
import "./styles.css";

export default class convBinario extends Component {

    state = {
        history: [],
        result: null
    }

    rollDice = async () => {
        const timeDice = document.querySelector('input#BinToDecBinary').value;
        const typeDice = document.querySelector('input#BinToDecBinary').value;

        const response = await diceAPI.get(`/json/${timeDice}d${typeDice}`);
        console.log(response);
        //this.setState({ products: docs, productInfo, page })
    }

    render() {
        return (
            <div className="convBin-form">
                <h2>Rolador de dados</h2>
                <p>A calculadora para conversão binaria converte facilmente números decimais em binários e vice-versa</p>
                <div className="convBin-div">
                    <h3>Converter binário para decimal</h3>
                    <label>Número binário</label>
                    <br />
                    <input
                        type="text"
                        placeholder="0 ou 1"
                        autoComplete="off"
                        id="BinToDecBinary"
                    />
                    <br />
                    <label>Número decimal</label>
                    <br />
                    <input
                        type="text"
                        id="BinToDecDecimal"
                        value={this.state.decimalValue}
                        readonly='true'
                    />
                    <br />
                    <button onClick={this.convertValueBinToDec}>Converter</button>
                </div>
            </div>
        );
    }
}
